                  <p>Please enter a valid name and age (non-empty values).</p>
          <p>Please enter a valid age ( &gt; 0).</p>

       <div className={styles.actions}> <button>Okay</button> </div> </div>
      </div>
    </div>
  );
};
export default ErrorModal;
