import React, {useState} from "react";
import styles from './AddUser.module.css';
import ErrorModal from "../ErrorModal/ErrorModal";

const AddUser = (props) => {
    const initialValue = { Username: '', Age: '' };
    const [collectedData, setCollectedData] = useState(initialValue);  
    const [isOpen, setIsOpen] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');


    const openModal = (name) => { 
        setIsOpen(true);

    };
    const closeModal = () => { 
        setIsOpen(false);
    };

    const submitHandler = (event) => {
        event.preventDefault();

       props.onAddData(collectedData);
        setCollectedData(initialValue);

    if (collectedData.Username === '' && collectedData.Age === '') {
        setErrorMessage('Please enter a valid name and age (non-empty values).');
        openModal();
    } else if (collectedData['Age'] <= 0 || collectedData['Age'] >= 110) {
        setErrorMessage()
        openModal();
    } else {
     return  console.log('DATA OK');
  };
    }

    const collectedDataHandler = (name, value) => {
        setCollectedData((prevState) => {
            return {
                ...prevState,
                [name]: value,
}})
        console.log(setCollectedData);


    }

    return (
        <div>
              <form className={styles.input} onSubmit={submitHandler}>
            <label>Username
                    <input  onChange={(event)=> collectedDataHandler('Username', event.target.value)}></input>
                </label>
            <label>Age(Years)
            <input  onChange={(event)=> collectedDataHandler('Age', event.target.value)}></input>
            </label>
                <button type="submit">Add User</button></form>

            {isOpen && <ErrorModal onClose={closeModal}><p>{errorMessage }</p></ErrorModal>}

      </div>
)
};

export default AddUser;
